#include<conio.h>
#include <windows.h>
//print text in red color
void red () {
  printf("\033[1;31m");
}
//print text in yellow color
void yellow (){
  printf("\033[1;33m");
}
//return to the default color
void reset () {
  printf("\033[0m");
}
