//introduction function
char intro (){
    char m;
         printf("\n\tWelcome To Chess Game\n\n \n");
         printf("\t #####     #   #    #######      ####    ####\n");
         printf("\t#          #   #    #           #       #   \n");
         printf("\t#          #   #    #           #       # \n");
         printf("\t#          #####    #######      ###     ###\n");
         printf("\t#          #   #    #               #       #\n");
         printf("\t#          #   #    #               #       #\n");
         printf("\t #####     #   #    #######     ####    ####\n");
        printf("\n   There are some instructions To help you:\n");
        printf(" While playing\n     Type U to undo \n     Type R to redo \n     Type S to save \n     Type Q to quit \n");
        printf("   To play write your current place Then enter the place you want to go to\n");
        printf("   upper case letters are the black pieces and the lower case are the white pieces\n");
        printf("   In case of promotion after your move enter the letter of piece you want ex.(a7a1q)\n");
        printf("====> You should play in full frame (maximize your window to have the best experience) <=====\n");
        printf("     Now, Type 's' to start or 'l' to load previous game:");
        scanf(" %c",&m);
        if(m=='s'||m=='S'){
            return 1;
        }
        /*if(m==l){
         load case
        }*/
        else{
                printf(" ERROR : Invalid Input Try Again\n");
           return 0;
        }

}
